function filterSelect() {
  $("#filter")
    .find(".filters")
    .find("li")
    .find("input")
    .on("change", function () {
      if ($(this).parent().parent().hasClass("active")) {
        $(this).parent().parent().removeClass("active");
        $(this)
          .parent()
          .next()
          .find("li")
          .removeClass("active")
          .find("input")
          .prop("checked", false);
      } else {
        $(this).parent().parent().addClass("active");
      }
    });
  $("#filter")
    .find(".filter-title")
    .on("click", function () {
      if ($(this).hasClass("active")) {
        $(this).removeClass("active").parent().removeClass("active");
        $(this)
          .next(".filters")
          .find("li")
          .find("input")
          .prop("checked", false)
          .parent()
          .parent()
          .removeClass("active");
      } else {
        $(this).addClass("active").parent().addClass("active");
      }
    });
}

