function megaMenu() {
  $(".mega-menu-parent").on("mouseenter click", function () {
    $(".mega-menu-container")
      .not($(this).find(".mega-menu-container"))
      .stop(true, true)
      .css({ display: "none" });
    $(this)
      .find(".mega-menu-container")
      .stop(true, true)
      .css({ display: "block" });
  });
  $(".mega-menu-parent").on("mouseleave", function () {
    $(this)
      .find(".mega-menu-container")
      .stop(true, true)
      .css({ display: "none" });
  });

  $(".mega-menu__side-submenu-parent")
    .find("> a")
    .on("click focusin mouseenter", function () {
      $(".mega-menu__side-submenu-parent")
        .find("> a")
        .not($(this))
        .stop(true, true)
        .removeClass("active");
      $(".mega-menu__side-submenu")
        .not($(this).parent().find(".mega-menu__side-submenu"))
        .stop(true, true)
        .css({ display: "none" });
      $(this)
        .addClass("active")
        .parent()
        .find(".mega-menu__side-submenu")
        .stop(true, true)
        .css({ display: "block" });
    });
  $(".mega-menu__big-submenu")
    .find("> li")
    .find("> a")
    .on("mouseenter", function () {
      $(".mega-menu__big-submenu")
        .find("> li")
        .find("> a")
        .not($(this))
        .removeClass("active")
        .parent()
        .find(".mega-menu__side-submenu")
        .stop(true, true)
        .css({ display: "none" });
    });
  $(".mega-menu__side-submenu-parent")
    .find("> a")
    .on("focusout", function () {
      $(this)
        .removeClass("active")
        .parent()
        .find(".mega-menu__side-submenu")
        .stop(true, true)
        .css({ display: "none" });
    });
}

